package com.bridhop

import android.graphics.Bitmap
import android.graphics.BitmapFactory
import android.util.Base64

class Library {
    companion object{
        val base64Image = ""

        fun decoder(): Bitmap{
            val bytesArrayImage = Base64.decode(base64Image, Base64.DEFAULT)

            return BitmapFactory.decodeByteArray(bytesArrayImage, 0, bytesArrayImage.size)
        }
    }
}
